INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('', 31, '<p><b>Dịch đáp án:</b></p><p><span>Không phải bạn đã từng bán những loại vitamin này tại cửa hàng của mình sao? </span></p><p><span>A. Bây giờ chúng tôi có một lựa chọn nhỏ hơn. </span></p><p><span>B. Giờ mở cửa của cửa hàng được liệt kê trên trang web. </span></p><p><span>C. Vâng, tôi đã mời cả hai.</span></p>', 'A.', 'B.', 'C.', NULL, 'A', 31, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (32, 'part3', 31, '<div><div><b>M:</b> Hello, I''m a guest here at the hotel. (32) I plan to do some sightseeing today, and I have a question.</div><div><b>W: </b>Certainly. What can I help you with?</div><div>M: I''m interested in visiting a couple of historic neighborhoods. (33) What would be a good way to travel around to see them?</div><div><b>W:</b> Well, you could rent a bicycle. There''s a shop just down the street where you can get one.</div><div><b>M:</b> Oh, that''s perfect if it''s not too expensive.</div><div><b>W: </b>(34)I don''t know what they charge, but I''d be happy to call them and find out.</div> </div><div><div><b>M: </b>Xin chào, tôi là khách lưu trú tại khách sạn đây. Tôi dự định đi tham quan một số nơi hôm nay, và tôi có một câu hỏi.</div><div><b>W:</b> Dĩ nhiên rồi. Tôi có thể giúp gì cho ông?</div><div>M: Tôi muốn đến thăm một vài khu phố lịch sử. Đến tham quan những điểm đó bằng phương tiện gì là ổn nhỉ?</div><div><b>W:</b> Chà, ông có thể thuê một chiếc xe đạp. Có một cửa hàng ngay dưới phố, ở đó ông có thể thuê một cái.</div><div><b>M:</b> Ồ, điều đó thật hoàn hảo nếu chi phí không quá đắt.</div><div><b>W: </b>Tôi không biết họ tính phí thế nào, nhưng tôi sẵn lòng gọi cho họ và tìm hiểu xem.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619426242.mp3', NULL, 'audio', NULL, 32, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the man say he plans to do today?', 32, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông nói anh ta dự định làm gì hôm nay?</span></p><p><span>A. Đi tham quan</span></p><p><span>B. Tham dự hội nghị</span></p><p><span>C. Mua một số quà tặng</span></p><p><span>D. Dừng lại ở ngân hàng</span></p>', 'Go sightseeing', 'Attend a conference', 'Purchase some gifts', 'Stop at a bank', 'A', 32, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the man ask about?', 33, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông hỏi về điều gì?</span></p><p><span>A. Truy cập Internet</span></p><p><span>B. Sử dụng trung tâm thể dục</span></p><p><span>C. Các phương án vận chuyển</span></p><p><span>D. Nhà hàng lân cận</span></p>', 'Internet access', 'Use of a fitness center', 'Transportation options', 'Nearby restaurants', 'C', 32, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the woman offer to do?', 34, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ đề nghị làm gì?</span></p><p><span>A. Đặt chỗ trước</span></p><p><span>B. In thẻ đậu xe</span></p><p><span>C. Cung cấp mật khẩu</span></p><p><span>D. Kiểm tra một số giá</span></p>', 'Make a reservation', 'Print a parking pass', 'Provide a password', 'Check on some prices', 'D', 32, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (33, 'part3', 32, '<div><div><b>W:</b> (35)The review of our restaurant is supposed to be in today''s newspaper. Have you seen it yet?</div><div><b>M:</b> (35)I read it before I came in to work. It''s the first review in the Entertainment section!</div><div><b>W:</b> Really? I hope it had a lot of good things to say about us.</div><div><b>M:</b> It did. In fact, (36)I think we''ll have a lot of new customers this week, thanks to the positive review. Especially since the reviewers really liked our specialty desserts!</div><div><b>W:</b> Well, (37)then we''d better prepare more food. I''ll order more ingredients right away.</div> </div><div><div><b>W: </b>Bài đánh giá về nhà hàng của chúng ta được lên lịch đăng trong số báo hôm nay. Bạn đã đọc nó chưa?</div><div><b>M:</b> Tôi đã đọc nó trước khi tôi vào làm việc. Đây là bài đánh giá đầu tiên trong mục Giải trí!</div><div><b>W:</b> Thật không? Tôi hy vọng nó có nhiều điều tốt đẹp để nói về chúng ta.</div><div><b>M:</b> Đúng là vậy. Thực ra, tôi nghĩ rằng chúng ta sẽ có rất nhiều khách hàng mới </div><div>trong tuần này, nhờ vào đánh giá tích cực. Đặc biệt là vì những người đánh giá thực sự thích các món tráng miệng đặc biệt của chúng ta!</div><div><b>W:</b> Vậy thì chúng ta nên chuẩn bị nhiều món ăn hơn. Tôi sẽ đặt thêm nguyên liệu ngay lập tức.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619426921.mp3', NULL, 'audio', NULL, 33, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What did the man do before coming in to work?', 35, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông đã làm gì trước khi đến làm việc?</span></p><p><span>A. Anh ấy đã gặp một người bạn</span></p><p><span>B. Anh ấy đã sửa lại thực đơn</span></p><p><span>C. Anh ấy đọc báo</span></p><p><span>D. Anh ấy đã mua đồ ăn sáng</span></p>', 'He met with a friend', 'He revised a menu', 'He read a newspaper', 'He bought some breakfast', 'C', 33, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the man think will happen this week?', 36, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông nghĩ điều gì sẽ xảy ra trong tuần này?</span></p><p><span>A. Sẽ có nhiều khách hàng hơn</span></p><p><span>B. Sẽ có kiểm tra an toàn</span></p><p><span>C. Hợp đồng sẽ được gia hạn</span></p><p><span>D. Một đầu bếp mới sẽ được thuê</span></p>', 'There will be more customer', 'There will be a safety inspection', 'A contract will be renewed', 'A new chef will be hired', 'A', 33, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What will the women most likely do next?', 37, '<p><b>Dịch đáp án:</b></p><p><span>Những người phụ nữ rất có thể sẽ làm gì tiếp theo?</span></p><p><span>A. Thuê thêm nhân viên</span></p><p><span>B. Lên lịch sửa chữa</span></p><p><span>C. Đặt hàng</span></p><p><span>D. In một bài viết</span></p>', 'Hire additional staff', 'Schedule some repairs', 'Place an order', 'Print an article', 'C', 33, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (34, 'part3', 33, '<div><div><b>M:</b> 38Welcome to Pavilion Appliance Store. How can I help you?</div><div><b>W:</b> Well, 39the handle on the door of my refrigerator broke off.</div><div><b>M:</b> Oh, that''s not good. If you bought it more than a year ago, it''s not going to be covered by the warranty.</div><div><b>W:</b> I know – I bought it three years ago. But I can fix it myself... I just need to buy the handle.</div><div><b>M:</b> I''m sorry, but we don''t sell replacement parts.</div><div><b>W: </b>So, do you know of a store that does sell them?</div><div><b>M: </b>Well, 40there is a Web site that sells appliance parts at good prices. You could see if they have what you''re looking for. It''s called AppliancePartsForYou.com.</div> </div><div><div><b>M: </b>Chào mừng đến với Cửa hàng Gia dụng Pavilion. Tôi có thể giúp gì cho cô?</div><div><b>W: </b>Chà, tay nắm cửa tủ lạnh của tôi đã bị gãy.</div><div><b>M:</b> Ồ, điều đó không tốt rồi. Nếu cô mua nó hơn một năm, nó sẽ không được bảo hành.</div><div><b>W: </b>Tôi biết – Tôi đã mua nó ba năm trước. Nhưng tôi có thể tự sửa nó ... Tôi chỉ cần mua tay nắm thôi.</div><div><b>M:</b> Tôi xin lỗi, nhưng chúng tôi không bán các bộ phận thay thế.</div><div><b>W:</b> Vậy bạn có biết cửa hàng nào bán chúng chứ?</div><div><b>M: </b>À, có một trang web bán các bộ phận của đồ gia dụng với mức giá tốt. Cô có thể xem nếu họ có những gì cô đang tìm kiếm không. Trang web tên là AppliancePartsForYou.com.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619427188.mp3', NULL, 'audio', NULL, 34, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Who most likely is the man?', 38, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông có nhiều khả năng nhất là ai?</span></p><p><span>A. Quản lý siêu thị</span></p><p><span>B. Nhân viên cửa hàng thiết bị</span></p><p><span>C. Một chủ cửa hàng kem</span></p><p><span>D. Một đại lý bất động sản</span></p>', 'A supermarket manager', 'An appliance store employee', 'An ice-cream shop owner', 'A real estate agent', 'B', 34, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What problem does the woman mention?', 39, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ đề cập đến vấn đề gì?</span></p><p><span>A. Hóa đơn không đúng</span></p><p><span>B. Một món đồ bị hỏng</span></p><p><span>C. Thiếu tài liệu</span></p><p><span>D. Một sản phẩm quá đắt</span></p>', 'An invoice is incorrect', 'An item is broken', 'A document is missing', 'A product is too expensive', 'B', 34, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the man suggest the woman do?', 40, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông đề nghị người phụ nữ làm gì?</span></p><p><span>A. Mua bảo hành</span></p><p><span>B. Trả lại một số hàng hóa</span></p><p><span>C. Gọi chuyên gia</span></p><p><span>D. Tìm một mục trực tuyến</span></p>', 'Purchase a warranty', 'Return some merchandise', 'Call a specialist', 'Find an item online', 'D', 34, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (35, 'part3', 34, '<div><div>M: Hi. 41I’m calling about your online leadership course. According to your Web site, it''s one of three courses required for your Professional Certificate in Management. I''m not interested in the certificate, so can I just take the leadership course?</div><div>W: Yes, but it''s probably worth it to get the certificate. Have you seen our online reviews? Most people who complete the certificate program are very satisfied.</div><div>M: Yes, 42I have a friend who got the management certificate through your program. It''s really helped her in her career.</div><div>W: Well, 43if you''re worried about the cost, you might qualify for a reduced course fee. Would you like me to look into that for you?</div> </div><div><div>M: Chào. Tôi gọi để hỏi về khóa học lãnh đạo trực tuyến của bạn. Theo trang web của bạn, đây là một trong ba khóa học bắt buộc đối với Chứng chỉ Quản lý Chuyên nghiệp do bên bạn cung cấp. Tôi không quan tâm đến chứng chỉ, vậy tôi có thể tham gia khóa học lãnh đạo không?</div><div>W: Có, nhưng có lẽ rất đáng giá để lấy chứng chỉ. Bạn đã xem các đánh giá trực tuyến của chúng tôi chưa? Hầu hết những người hoàn thành chương trình chứng chỉ đều rất hài lòng.</div><div>M: Vâng, tôi có một người bạn đã có chứng chỉ quản lý thông qua chương trình của bạn. Nó thực sự đã giúp cô ấy trong sự nghiệp của mình.</div><div>W: Chà, nếu bạn lo lắng về chi phí, bạn có thể đủ điều kiện để được giảm học phí. Bạn có muốn tôi xem xét điều đó cho bạn không?</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619427496.mp3', NULL, 'audio', NULL, 35, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why is the man calling?', 41, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao người đàn ông lại gọi?</span></p><p><span>A. Để thanh toán</span></p><p><span>B. Để yêu cầu gia hạn</span></p><p><span>C. Để hủy đăng ký</span></p><p><span>D. Để có được thông tin khóa học</span></p>', 'To make a payment', 'To request an extension', 'To cancel a registration', 'To obtain course infomation', 'D', 35, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('what does the man say about his friend?', 42, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông nói gì về bạn mình?</span></p><p><span>A. Cô ấy làm việc ở văn phòng của anh ấy</span></p><p><span>B. Cô ấy được hưởng lợi từ chương trình chứng chỉ</span></p><p><span>C. Cô ấy điều hành một công ty kế toán thành công</span></p><p><span>D. Cô ấy đã viết một bài đánh giá tích cực</span></p>', 'She works in his office', 'She benefited from a certificate program', 'She runs a successful accounting firm', 'She wrote a postitive review', 'B', 35, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the woman say the man might be eligible for?', 43, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ nói người đàn ông có thể đủ điều kiện để làm gì?</span></p><p><span>A. Giảm phí</span></p><p><span>B. Tư vấn</span></p><p><span>C. Thăng tiến trong công việc</span></p><p><span>D. Nâng cấp miễn phí</span></p>', 'A reduced fee', 'A consultation', 'A job promotion', 'A free upgrade', 'A', 35, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (36, 'part3', 35, '<div><div>W: Hi, Carlos. 44,45Did you see the e-mail from the manager? Our fitness center’s going to stay open 24 hours!</div><div>M: Yes, I read it. 45The e-mail said the new hours will start next month. I think it''ll make our members very happy. They’ve wanted later hours for a while.</div><div>W: Definitely. The manager also asked us to make some signs and post them around the gym so members are aware of this change.</div><div>M: 46I have some time now. I''ll get started making those signs right away.</div> </div><div><div>W: Xin chào, Carlos. Bạn có thấy e-mail từ người quản lý không? Trung tâm thể hình của chúng ta sẽ mở cửa 24 giờ!</div><div>M: Vâng, tôi đã đọc nó rồi. E-mail cho biết giờ hoạt động mới sẽ bắt đầu vào tháng tới. Tôi nghĩ nó sẽ làm cho các thành viên của chúng ta rất thích thú. Họ luôn muốn giờ hoạt động kéo dài từ trước rồi.</div><div>W: Chắc chắn rồi. Người quản lý cũng yêu cầu chúng ta làm một số tấm biển và dán chúng xung quanh phòng tập để các thành viên biết về sự thay đổi này.</div><div>M: Bây giờ tôi rảnh. Tôi sẽ bắt đầu làm những tấm biển đó ngay lập tức.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619427712.mp3', NULL, 'audio', NULL, 36, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Where do the speakers work?', 44, '<p><b>Dịch đáp án:</b></p><p><span>Người nói làm việc ở đâu?</span></p><p><span>A. Tại cửa hàng quần áo</span></p><p><span>B. Tại trung tâm thể hình</span></p><p><span>C. Tại một công ty phần mềm</span></p><p><span>D. Tại nhà thi đấu thể thao</span></p>', 'At a clothing store', 'At a  fitness center', 'At a software company', 'At a sports arena', 'B', 36, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('According to the speakers. what will happen next month?', 45, '<p><b>Dịch đáp án:</b></p><p><span>Theo các </span><span>người nói</span><span>. điều gì sẽ xảy ra vào tháng tới?</span></p><p><span>A. Tùy chọn giá sẽ thay đổi</span></p><p><span>B. Công việc cải tạo sẽ bắt đầu</span></p><p><span>C. Nhân viên mới sẽ được thuê</span></p><p><span>D. Giờ làm việc sẽ được kéo dài</span></p>', 'Pricing options will change', 'Renovation work will begin', 'New employees will be hired', 'Business hours will be extended', 'D', 36, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the man offter to do?', 46, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông đề nghị làm gì?</span></p><p><span>A. Làm một số dấu hiệu</span></p><p><span>B. Cập nhật lịch trình</span></p><p><span>C. Ước tính một số chi phí</span></p><p><span>D. Gửi e-mail</span></p>', 'Make some signs', 'Update a schedule', 'Estimate some costs', 'Send an e-mail', 'A', 36, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (37, 'part3', 36, '<div><div>M1: Adam, 47I just got a message to come to the factory floor. (48) Is something wrong?</div><div>M2: 47,48There''s a problem with the last few desks that came off the production line. Look at this one – the top surface of the desk is loose because no glue was applied from the machines.</div><div>M1: Carol – could you join us for a minute? Can you check the glue machines on your team''s part of the assembly line? They probably need a refill.</div><div>W: OK. I''ll shut them down and take a look. We should be back in service within an hour.</div><div>M1: Good. 49This afternoon at one o''clock, some prospective clients will be visiting. Everything needs to be running smoothly by then.</div> </div><div><div>M1: Adam, tôi vừa nhận được lời nhắn đến nhà máy. Có gì đó không ổn sao?</div><div>M2: Có vấn đề với một số bàn làm việc cuối cùng được đưa ra khỏi dây chuyền sản xuất. Hãy nhìn cái này – mặt trên của bàn bị lỏng vì không có keo từ máy dán.</div><div>M1: Carol – bạn có thể tham gia với chúng tôi một phút không? Bạn có thể kiểm tra máy dán keo trong dây chuyền lắp ráp của nhóm bạn chứ? Chúng có thể cần nạp keo lại.</div><div>W: Được rồi. Tôi sẽ tắt chúng và xem xét. Chúng tôi sẽ trở lại phục vụ trong vòng một giờ nữa.</div><div>M1: Tốt lắm. Chiều nay lúc một giờ, một số khách hàng tiềm năng sẽ đến thăm. Mọi thứ cần phải chạy trơn tru vào thời điểm đó.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
