INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Where most likely are the speakers?', 47, '<p><b>Dịch đáp án:</b></p><p><span>Người nói có thể ở đâu nhất?</span></p><p><span>A. Tại một cửa hàng in</span></p><p><span>B. Tại một nhà máy sản xuất đồ nội thất</span></p><p><span>C. Tại kho thiết bị</span></p><p><span>D. Tại cửa hàng văn phòng phẩm</span></p>', 'At a print shop', 'At a furniture factory', 'At an appliance warehouse', 'At a stationery store', 'B', 37, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What problem do the men describle?', 48, '<p><b>Dịch đáp án:</b></p><p><span>Những người đàn ông mô tả vấn đề gì?</span></p><p><span>A. Một cánh cửa đã bị mở</span></p><p><span>B. Một chiếc xe tải giao hàng bị trì hoãn</span></p><p><span>C. Một số nhân viên bị ốm</span></p><p><span>D. Một số sản phẩm bị lỗi</span></p>', 'A door has been left open', 'A delivery truck has been delayed', 'Some employees are out sick', 'Some products are defective', 'D', 37, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What is scheduled to happen at one o''clock?', 49, '<p><b>Dịch đáp án:</b></p><p><span>Điều gì dự kiến ​​sẽ xảy ra vào lúc một giờ?</span></p><p><span>A. Một số khách hàng sẽ ghé thăm</span></p><p><span>B. Một số thiết bị sẽ được làm sạch</span></p><p><span>C. Một buổi đào tạo sẽ được tiến hành</span></p><p><span>D. Sẽ tổ chức tiệc trưa cho nhân viên</span></p>', 'Some clients will visit', 'Some equipment will be cleaned', 'A training session will be conducted', 'An employee luncheon will be held', 'A', 37, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (38, 'part3', 37, '<div><div>W: Hi, Gerard. Just a reminder, 50all proposals for this year''s technology conference have to be submitted by tomorrow.</div><div>M: Yes, I know. 50I actually finished my proposal earlier today, but I haven''t been</div><div>able to upload it yet... 51I think the Web site''s down for maintenance.</div><div>W: Oh, I had the same problem – the Web site''s really busy. You just have to keep trying.</div><div>M: In that case, 52I think I''ll go to lunch first and try again when I get back. Would you like to join me?</div><div>W: 52Sure – let’s go now.</div> </div><div><div>W: Xin chào, Gerard. Xin nhắc lại, tất cả các đề xuất cho hội nghị công nghệ năm</div><div>nay phải được đệ trình vào ngày mai.</div><div>M: Vâng, tôi biết. Tôi thực sự đã hoàn thành đề xuất của mình sớm nay, nhưng tôi vẫn chưa thể đăng nó lên... Tôi nghĩ rằng trang web đã ngừng hoạt động để bảo trì.</div><div>W: Ồ, tôi cũng gặp phải vấn đề tương tự – trang web thực sự rất nghẽn mạng. Bạn cứ tiếp tục thử xem sao.</div><div>M: Trong trường hợp đó, tôi nghĩ tôi sẽ đi ăn trưa trước và thử lại khi quay về. Bạn có muốn ăn cùng tôi không?</div><div>W: Chắc chắn rồi – chúng ta đi ngay thôi.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
