INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What did the man recently finish?', 50, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông vừa mới hoàn thành việc gì?</span></p><p><span>A. Đề xuất hội nghị</span></p><p><span>B. Lịch trình chuyến công tác</span></p><p><span>C. Bản sửa đổi cơ sở dữ liệu khách hàng</span></p><p><span>D. Xem xét một số đồ án</span></p>', 'A conference proposal', 'An agenda for a business trip', 'A revision to a client database', 'A review of some blueprints', 'A', 38, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What problem does the man mention', 51, '<p><b>Dịch đáp án:</b></p><p><span>Người đàn ông đề cập đến vấn đề gì</span></p><p><span>A. Cần có giấy phép bổ sung</span></p><p><span>B. Một khoản chi phí không được phê duyệt</span></p><p><span>C. Không có người giám sát</span></p><p><span>D. Một trang web không hoạt động</span></p>', 'Additional permits are needed', 'An expense was not approved', 'A supervisor is unavailable', 'A web site is not working', 'D', 38, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What will  the speakers do next?', 52, '<p><b>Dịch đáp án:</b></p><p><span>Các </span><span>người nói</span><span> sẽ làm gì tiếp theo?</span></p><p><span>A. Luyện tập một bài thuyết trình</span></p><p><span>B. In một số tài liệu</span></p><p><span>C. Đi ăn trưa</span></p><p><span>D. Gọi hỗ trợ kỹ thuật</span></p>', 'Rehearse a presentaion', 'Print some documents', 'Go have lunch', 'Call technical support', 'C', 38, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (39, 'part3', 38, '<div><div>M: Hi, Emiko. 53Welcome to Essen Accounting. I''m Amal Hassan, from Human Resources. I''ll show you around the office and introduce you to the rest of the accounting team.</div><div>W: Nice to meet you, Amal. Do you know if I''ll get my permanent ID badge soon? 54I just have this temporary ID card, and this isn''t the correct spelling of my name.</div><div>M: Well, you can use that ID to move through the building today, but we''ll ask at the security desk later. Do you have any other questions before we start the tour?</div><div>W: Um, 55I thought I''d get copies of my employee paperwork, but I haven''t received anything yet. Is there something I should do?</div><div>M: Oh, it normally takes a few days.</div><div><br/></div> </div><div><div>M: Chào, Emiko. Chào mừng bạn đến với Essen Accounting. Tôi là Amal Hassan,</div><div>từ Bộ phận Nhân sự. Tôi sẽ dẫn bạn dạo một vòng quanh văn phòng và giới thiệu bạn với những người còn lại trong đội ngũ kế toán.</div><div>W: Rất vui được gặp bạn, Amal. Bạn có biết liệu tôi sẽ sớm nhận được huy hiệu nhận diện dài hạn của mình không? Tôi chỉ có thẻ nhận viên tạm thời này và đây không phải là cách viết chính xác của tên tôi.</div><div>M: Chà, bạn có thể sử dụng thẻ nhận diện đó để di chuyển quanh tòa nhà ngày hôm nay, nhưng chúng tôi sẽ hỏi ở quầy an ninh sau. Bạn có câu hỏi nào khác trước khi chúng ta bắt đầu chuyến tham quan không?</div><div>W: Ùm, tôi nghĩ tôi sẽ nhận được bản sao giấy tờ lao động, nhưng tôi vẫn chưa nhận được gì. Có điều gì tôi nên làm không?</div><div>M: Ôi, chuyện đó thường mất vài ngày lận.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
