INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Who most likely is the woman?', 53, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ có nhiều khả năng nhất là ai?</span></p><p><span>A. Thanh tra an toàn</span></p><p><span>B. Một khách hàng nước ngoài</span></p><p><span>C. Một vận động viên</span></p><p><span>D. Kế toán viên</span></p>', 'A safety inspector', 'An overseas client', 'An athlete', 'An accountant', 'D', 39, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the woman tell the man about her ID card?', 54, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ nói gì với người đàn ông về chứng minh thư của cô ấy?</span></p><p><span>A. Nó đã hết hạn</span></p><p><span>B. Nó có ảnh sai</span></p><p><span>C. Tên cô ấy viết sai chính tả</span></p><p><span>D. Cô ấy quên mang theo</span></p>', 'It is expired', 'It has the wrong photo', 'Her name is misspelled', 'She forgot to bring it', 'C', 39, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why does the man say, "it normally takes a few days"?', 55, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao người đàn ông lại nói, "thông thường phải mất vài ngày"?</span></p><p><span>A. Để từ chối một yêu cầu</span></p><p><span>B. Để giải quyết mối quan ngại</span></p><p><span>C. Khiếu nại về sự chậm trễ</span></p><p><span>D. Để thừa nhận một thành tựu</span></p>', 'To reject a request', 'To address a concern', 'To complain about a delay', 'To acknowledge an accomplishment', 'B', 39, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (40, 'part3', 39, '<div><div>W: Hi, 56your company must be new to this technology trade show – I''ve been attending for years and I''ve never seen your booth.</div><div>M1: You''re right – 57we''re a new start-up company...We''ve developed a voice-recognition software program. It understands spoken commands and can complete tasks.</div><div>W: Ah, I use a program like that. So... 58how''s your program different from all</div><div>the others?</div><div>M1: It’s the most advanced digital assistant on the market. Here – come and meet Antonio. He’s our lead software developer.</div><div>M2: Hi, I''m Antonio. 58Here''s a brochure. If you turn to the second page, there''s</div><div>an outline of the program''s advanced features... You won''t find those features anywhere else.</div> </div><div><div>W: Xin chào, công ty của ông hẳn là mới tham gia triển lãm thương mại công nghệ này – Tôi đã tham dự nhiều năm và chưa bao giờ nhìn thấy gian hàng của ông.</div><div>M1: Cô nói đúng – chúng tôi là một công ty mới thành lập... Chúng tôi đã phát triển một chương trình phần mềm nhận dạng giọng nói. Nó hiểu các lệnh bằng giọng nói và có thể hoàn thành những nhiệm vụ.</div><div>W: À, tôi sử dụng một chương trình như vậy. Vậy... phần mềm của ông gì với những phần mềm khác?</div><div>M1: Đây là trợ lý kỹ thuật số tiên tiến nhất trên thị trường. Đây – hãy đến gặp Antonio. Anh ấy là nhà phát triển phần mềm hàng đầu của chúng tôi.</div><div>M2: Xin chào, tôi là Antonio. Đây là một tập tài liệu. Nếu cô lật sang trang thứ hai, có một phác thảo về các tính năng cao cấp của chương trình... Cô sẽ không tìm thấy những tính năng đó ở bất kỳ nơi nào khác.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
