INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What type of event are the speakers attending?', 56, '<p><b>Dịch đáp án:</b></p><p><span>Các </span><span>người nói</span><span> sẽ tham dự loại sự kiện nào?</span></p><p><span>A. Triển lãm thương mại</span></p><p><span>B. Định hướng cho nhân viên mới</span></p><p><span>C. Một cuộc phỏng vấn người nổi tiếng</span></p><p><span>D. Khai trương</span></p>', 'A trade show', 'A new-hire orientation', 'A celebrity interview', 'A grand opening', 'A', 40, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Where do the men work', 57, '<p><b>Dịch đáp án:</b></p><p><span>Đàn ông làm việc ở đâu</span></p><p><span>A. Tại một nhà sản xuất điện thoại di động</span></p><p><span>B. Tại một công ty phát triển phần mềm</span></p><p><span>C. Tại cửa hàng sửa chữa thiết bị</span></p><p><span>D. Tại công ty quảng cáo</span></p>', 'At a mobile phone manufacturer', 'At a software development company', 'At an appliance repair shop', 'At a advertising agency', 'B', 40, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What will the woman most likely do next?', 58, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ rất có thể sẽ làm gì tiếp theo?</span></p><p><span>A. Trao đổi thông tin liên hệ</span></p><p><span>B. Tham khảo ý kiến ​​đồng nghiệp</span></p><p><span>C. Kiểm tra lịch trình</span></p><p><span>D. Nhìn vào một tập tài liệu</span></p>', 'Exchange contact infomation', 'Consult with a colleague', 'Check a schedule', 'Look at a brochure', 'D', 40, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (41, 'part3', 40, '<div><div>M: Noor! 59Exciting news! The company''s board of directors chose our research project to receive additional funding.</div><div>W: Oh – that''s great!</div><div>M: I know. So, 60let’s hire a new research assistant – like we''ve been talking about.</div><div>W: Hmm, 60even with the extra money, our budget''s going to be fairly limited.</div><div>M: But we need help cataloging all the data we''re collecting... You know, we could offer some part-time hours to local university students.</div><div>W: You’re right. 61I was on a project last year with some students, and they did</div><div>a good job.</div><div><br/></div> </div><div><div>M: Noor! Tin tốt đây! Ban giám đốc của công ty đã duyệt dự án nghiên cứu của</div><div>chúng ta được thêm tài trợ.</div><div>W: Ồ – thật tuyệt!</div><div>M: Tôi biết. Vì vậy, chúng ta hãy thuê một trợ lý nghiên cứu mới – giống như chúng ta đã đề cập.</div><div>W: Hừm, ngay cả khi có thêm tiền, ngân sách của chúng ta vẫn sẽ eo hẹp.</div><div>M: Nhưng chúng ta cần trợ giúp lập danh mục tất cả dữ liệu mà chúng ta đang thu thập... Bạn biết đấy, chúng ta có thể đưa ra một công việc bán thời gian cho sinh viên đại học địa phương.</div><div>W: Bạn nói đúng. Tôi đã tham gia một dự án vào năm ngoái với một số sinh viên, và họ đã làm tốt.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
