INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why is men excited?', 59, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao đàn ông lại hào hứng?</span></p><p><span>A. Anh ấy đã được thăng chức</span></p><p><span>B. Yêu cầu nghỉ phép của anh ấy đã được chấp thuận</span></p><p><span>C. Một bài báo nghiên cứu sẽ được xuất bản</span></p><p><span>D. Một dự án sẽ nhận được nguồn tài trợ bổ sung</span></p>', 'He has been promoted', 'His vacation request was approved', 'A research article will be published', 'A project will receive extra funding', 'D', 41, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the woman mean when she says, "our budget''s going to be fairly limited"?', 60, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ có ý gì khi nói "ngân sách của chúng ta sắp khá hạn chế"?</span></p><p><span>A. Quyết định của công ty không có gì đáng ngạc nhiên</span></p><p><span>B. Nên tổ chức một buổi gây quỹ</span></p><p><span>C. Một số con số cần kiểm tra</span></p><p><span>D. Lời đề nghị của người đàn ông có thể không hiệu quả</span></p>', 'The company''s decision is not surprising', 'A fund-raiser should be organized', 'Some numbers need to be checked', 'The man''s suggestion may not work', 'D', 41, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the woman say about local students?', 61, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ nói gì về sinh viên địa phương?</span></p><p><span>A. Cô ấy đã từng làm việc với họ trước đây</span></p><p><span>B. Cô ấy đang dạy kèm khoa học cho họ</span></p><p><span>C. Họ đang tham gia một hội nghị</span></p><p><span>D. Họ đã quyên góp rất nhiều</span></p>', 'She has worked with them before', 'She is tutoring them in science', 'The are participating in a conference', 'The have made serveral donations', 'A', 41, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (42, 'part3', 41, '<div><div>W: Hi, I''m here to pick up a cake for the Frayton Company.</div><div>M: Hmmm… I don’t see that name here. Maybe the cake was ordered under an individual’s name? Uh… I have a Richard…</div><div>W: Oh, 62I''m not sure which of my coworkers ordered it, but it should have the number 25 on it.</div><div>M: Oh, it must be this one </div><div>W: Yeah, that''s it. 63It''s for our company''s twenty-fifth anniversary celebration tonight. We''re very excited.</div><div>M: OK, your total is 40 dollars.</div><div>W: 64Can I use my phone to pay</div><div>M: Sorry, we''re not set up for that service yet. It''s just cash or credit card.</div> </div><div><div>M: Hừm… Tôi không thấy tên đó ở đây. Có thể chiếc bánh được đặt dưới tên của một cá nhân nào chăng? Ờ… Tôi có một cái tên Richard…</div><div>W: Ồ, tôi không chắc đồng nghiệp nào của tôi đã đặt nó, nhưng nó chắc chắn có số 25 trên đó.</div><div>M: Ồ, vậy phải là cái này rồi.</div><div>W: Đúng vậy. Nó dành cho lễ kỷ niệm 25 năm của công ty chúng tôi vào tối nay. Chúng tôi rất hào hứng.</div><div>M: Được rồi, tổng số tiền của cô là 40 đô la.</div><div>W: Tôi có thể thanh toán qua điện thoại được không?</div><div>M: Xin lỗi, chúng tôi chưa thiết lập dịch vụ đó. Chỉ có thể thanh toán tiền mặt hoặc thẻ tín dụng.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
