INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Look at the graphic. Who placed the order the woman is picking up?', 62, '<p><b>Dịch đáp án:</b></p><p><span>Nhìn vào đồ họa. Ai đã đặt hàng mà người phụ nữ đang nhận hàng?</span></p><p><span>A. Richard</span></p><p><span>B. Alison</span></p><p><span>C. Tomas</span></p><p><span>D. Janet</span></p>', 'Richard', 'Alison', 'Tomas', 'Janet', 'C', 42, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What event does the woman mention?', 63, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ đề cập đến sự kiện gì?</span></p><p><span>A. Một cuộc gặp gỡ khách hàng</span></p><p><span>B. Tiệc sinh nhật</span></p><p><span>C. Bữa tiệc nghỉ hưu</span></p><p><span>D. Lễ kỷ niệm</span></p>', 'A client meeting', 'A birhtday party', 'A retirement lucheon', 'An anniversary celebration', 'D', 42, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the woman ask the man about?', 64, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ hỏi người đàn ông về điều gì?</span></p><p><span>A. Hình thức thanh toán</span></p><p><span>B. Dịch vụ giao hàng</span></p><p><span>C. Một tấm thiệp chúc mừng</span></p><p><span>D. Một thành phần</span></p>', 'A form of payment', 'A delivery service', 'A greeting card', 'An ingredient', 'A', 42, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (43, 'part3', 42, '<div><div>M: Hello, this is Samir from Rumsford Dental Clinic. I’m calling to confirm your appointment tomorrow at eight forty-five.</div><div>W: Oh no! I forgot… 65I have a meeting tomorrow morning at work. Can I reschedule for later in the day?</div><div>M: Uh… let’s see… We have another opening at three in the afternoon.</div><div>W: That’s perfect. 66What’s your address again?</div><div>M: Actually, 66we’ve moved since your last appointment. We’re still on Blevin Road, but now we’re at number 47, at the intersection with Shara Lane. You’ll see a café across the street.</div><div>W: Great, thanks. 66I’ll be there tomorrow.</div><div>M: And 67please arrive ten minutes early so we can update your records.</div> </div><div><div>M: Xin chào, đây là Samir đến từ Phòng khám Nha khoa Rumsford. Tôi đang gọi để xác nhận cuộc hẹn ngày mai của cô lúc 8 giờ 45.</div><div>W: Ôi không! Tôi quên mất... Tôi có một cuộc họp vào sáng mai ở cơ quan. Tôi có thể đặt lịch lại muộn hơn trong ngày được không?</div><div>M: Ờ… để xem… Chúng tôi có một lịch trống khác lúc 3 giờ chiều.</div><div>W: Điều đó thật hoàn hảo. Cho tôi xin lại địa chỉ của bạn nhé?</div><div>M: Thực ra, chúng tôi đã dời đi kể từ cuộc hẹn cuối cùng của cô. Chúng tôi vẫn đang ở trên Đường Blevin, nhưng hiện tại chúng tôi đang ở số 47, tại giao lộ với Đường Shara. Cô sẽ thấy một quán cà phê bên kia đường.</div><div>W: Thật tuyệt, cảm ơn. Ngày mai tôi sẽ đến đó.</div><div>M: Và hãy đến sớm 10 phút để chúng tôi có thể cập nhật hồ sơ của cô.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
