INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Where do the speakers work?', 68, '<p><b>Dịch đáp án:</b></p><p><span>Người nói làm việc ở đâu?</span></p><p><span>A. Tại một cửa hàng hoa</span></p><p><span>B. Tại một công ty bất động sản</span></p><p><span>C. Tại cửa hàng sơn</span></p><p><span>D. Tại nhà xuất bản báo</span></p>', 'At a flower shop', 'At a real estate company', 'At a paint store', 'At a newspaper publisher', 'A', 44, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the woman say about color advertisements?', 69, '<p><b>Dịch đáp án:</b></p><p><span>Người phụ nữ nói gì về quảng cáo màu sắc?</span></p><p><span>A. Khó thiết kế</span></p><p><span>B. Thầy thu hút nhiều sự chú ý hơn</span></p><p><span>C. Có thể gửi bằng điện tử</span></p><p><span>D. Họ nên sử dụng màu sắc cụ thể</span></p>', 'The are difficult to design', 'Thay attract more attention', 'The can be sent electronically', 'They should use specific colors', 'B', 44, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Look at the graphic. How much will the speakers pay for their advertisement?', 70, '<p><b>Dịch đáp án:</b></p><p><span>Nhìn vào đồ họa. Các </span><span>người nói</span><span> sẽ trả bao nhiêu cho quảng cáo của họ?</span></p><p><span>A. $300</span></p><p><span>B. $500</span></p><p><span>C. $250</span></p><p><span>D. $400</span></p>', '$300', '$500', '$250', '$400', 'D', 44, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (45, 'part4', 44, '<div><div>M: 71Are you a coffee drinker? Are you tired of your old coffee maker? 72Well, Peterson Industries is running a focus group next month, and we''re currently looking for volunteers to try out our newest coffee and espresso maker. You''ll have a chance to provide feedback and tell us what you think of the coffee maker''s features. And 73as a bonus for your participation, you''ll receive a coupon for 50 percent off the coffee maker once it hits the shelves. To sign up, visit our Web site.</div> </div><div><div>M: Bạn có phải là người thích uống cà phê không? Bạn cảm thấy mệt mỏi với chiếc máy pha cà phê cũ của mình rồi à? Chà, Peterson Industries sẽ điều hành một nhóm tập trung vào tháng sau và chúng tôi hiện đang tìm kiếm các tình nguyện viên để dùng thử máy pha cà phê và cà phê espresso mới nhất của chúng tôi. Bạn sẽ có cơ hội cung cấp phản hồi và cho chúng tôi biết suy nghĩ của bạn về các tính năng của máy pha cà phê. Và như một phần thưởng cho sự tham gia của bạn, bạn sẽ nhận được phiếu giảm giá 50% cho máy pha cà phê khi nó lên kệ. Để đăng ký, hãy truy cập trang web của chúng tôi.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619667031.mp3', NULL, 'audio', NULL, 45, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What type of prodcut is being discussed?', 71, '<p><b>Dịch đáp án:</b></p><p><span>Loại sản phẩm cùng loại nào đang được thảo luận?</span></p><p><span>A. Máy pha cà phê</span></p><p><span>B. Lò vi sóng</span></p><p><span>C. Máy sưởi di động</span></p><p><span>D. Máy lọc nước</span></p>', 'A coffee maker', 'A microwave oven', 'A portable heater', 'A water filter', 'A', 45, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What is the purpose of ther advertisement?', 72, '<p><b>Dịch đáp án:</b></p><p><span>Mục đích của quảng cáo đó là gì?</span></p><p><span>A. Để tiết lộ ngày phát hành sản phẩm</span></p><p><span>B. Để công bố một cuộc thi tài năng</span></p><p><span>C. Để tìm tình nguyện viên cho một nhóm tập trung</span></p><p><span>D. Để giải thích chi tiết về bảo hành</span></p>', 'To reveal a product release date', 'To announce a talent show', 'To find volunteers for a focus group', 'To explain details of a warranty', 'C', 45, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What bonus is mentioned?', 73, '<p><b>Dịch đáp án:</b></p><p><span>Phần thưởng nào được đề cập?</span></p><p><span>A. Một vé xổ số</span></p><p><span>B. Một phiếu giảm giá</span></p><p><span>C. Hoàn trả chi phí đi lại</span></p><p><span>D. Miễn phí vận chuyển</span></p>', 'A raffle ticket', 'A coupon', 'Travel reimbursement', 'Free shipping', 'B', 45, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (46, 'part4', 45, '<div><div>W: Hello, it''s Lena. 74My flight''s been delayed because of bad weather, and I''m stuck at the airport here in Vancouver. Anyway, 75I don''t think I''ll make it back to work tomorrow. The clients from Spain are coming in at two o''clock and I won''t be able to meet with them. Fortunately for us, Paul lived in Spain for ten years! 76Could you stop by his office, explain the situation, and give him the client agenda?</div> </div><div><div>W: Xin chào, tôi là Lena. Chuyến bay của tôi bị hoãn vì thời tiết xấu và tôi bị kẹt ở sân bay ở Vancouver. Dù sao, tôi không nghĩ rằng tôi sẽ kịp trở lại làm việc vào ngày mai. Các khách hàng từ Tây Ban Nha sẽ đến lúc hai giờ trưa và tôi sẽ không thể gặp họ.  May mắn thay cho chúng tôi, Paul đã sống ở Tây Ban Nha được 10 năm! Bạn có thể ghé qua văn phòng của anh ấy, giải thích tình hình và đưa cho anh ấy chương trình làm việc của khách hàng được không?</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619667205.mp3', NULL, 'audio', NULL, 46, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Where is the speaker?', 74, '<p><b>Dịch đáp án:</b></p><p><span>Người nói ở đâu?</span></p><p><span>A. Tại một khách sạn</span></p><p><span>B. Tại công ty du lịch</span></p><p><span>C. Trên tàu</span></p><p><span>D. Ở sân bay</span></p>', 'At a hotel', 'At a travel agency', 'On a train', 'In an airport', 'D', 46, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What will happen at the company tomorrow?', 75, '<p><b>Dịch đáp án:</b></p><p><span>Điều gì sẽ xảy ra ở công ty vào ngày mai?</span></p><p><span>A. Đồ nội thất mới sẽ đến</span></p><p><span>B. Một số khách hàng sẽ ghé thăm</span></p><p><span>C. Một nhân viên mới sẽ được giới thiệu</span></p><p><span>D. Một số phần mềm máy tính sẽ được cập nhật</span></p>', 'New furniture will arrive', 'Some clients will visit', 'A new employee will be introduced', 'Some computer software will be updated', 'B', 46, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why does the speaker say, "Paul lived in Spain for ten years"?', 76, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao người nói nói, "Paul sống ở Tây Ban Nha được mười năm"?</span></p><p><span>A. Để giới thiệu một đồng nghiệp cho một nhiệm vụ</span></p><p><span>B. Để gợi ý địa điểm du lịch</span></p><p><span>C. Để bày tỏ sự ngạc nhiên về một ngày kỷ niệm</span></p><p><span>D. Để sửa lỗi nhập dữ liệu</span></p>', 'To recommend a colleague for a task', 'To suggest a travel destination', 'To express suprise about an anniversary date', 'To correct a data-entry error', 'A', 46, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (47, 'part4', 46, '<div><div>W: 77To begin today''s staff meeting, I''m happy to announce that our plans to create an outdoor work area have been finalized. When the project is completed, there''ll be individual workstations and tables for small group meetings. 78If you want to see what the space will look like, there''s an architectural layout posted on our company''s home page. Be sure to let us know if you have any questions or concerns. 79Some of you have already expressed concern about accessing the Internet outside. I can assure you... wireless Internet connectivity will be available.</div> </div><div><div>W: Để bắt đầu cuộc họp nhân viên hôm nay, tôi vui mừng thông báo rằng kế hoạch tạo khu vực làm việc ngoài trời của chúng ta đã được chốt. Khi dự án hoàn thành, sẽ có các bàn làm việc cá nhân và bàn riêng cho các cuộc họp nhóm nhỏ. Nếu bạn muốn xem không gian sẽ như thế nào, có một bản vẽ kiến trúc được đăng trên trang chủ của công ty chúng ta. Đừng quên báo cho chúng tôi biết nếu bạn có bất kỳ câu hỏi hoặc thắc mắc nào. Một số người đã bày tỏ lo lắng về việc truy cập Internet bên ngoài. Tôi có thể đảm bảo với các bạn... kết nối Internet không dây sẽ có sẵn.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619667398.mp3', NULL, 'audio', NULL, 47, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What is the announcement about?', 77, '<p><b>Dịch đáp án:</b></p><p><span>Thông báo nói về cái gì?</span></p><p><span>A. Đội ngũ bán hàng mới</span></p><p><span>B. Không gian làm việc ngoài trời</span></p><p><span>C. Văn phòng chi nhánh</span></p><p><span>D. Một chiến dịch tiếp thị</span></p>', 'A new sales team', 'An outdoor work space', 'A branch office', 'A marketing campaign', 'B', 47, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What can the listeners find on the company Web site?', 78, '<p><b>Dịch đáp án:</b></p><p><span>Người nghe có thể tìm thấy gì trên trang web của công ty?</span></p><p><span>A. Một bài báo</span></p><p><span>B. Video trình diễn</span></p><p><span>C. Sơ đồ kiến ​​trúc</span></p><p><span>D. Chính sách sửa đổi</span></p>', 'A newspaper article', 'A video demostration', 'An architectural plan', 'A revised policy', 'C', 47, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('According to the speaker, what have some people expressed concern about?', 79, '<p><b>Dịch đáp án:</b></p><p><span>Theo </span><span>người nói</span><span>, một số người bày tỏ lo ngại về điều gì?</span></p><p><span>A. Lịch làm việc</span></p><p><span>B. Ngày hoàn thành</span></p><p><span>C. Kết nối Internet</span></p><p><span>D. Chỗ đỗ xe</span></p>', 'A work schedule', 'A completion date', 'Internet connectivity', 'Parking avilablity', 'C', 47, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (48, 'part4', 47, '<div><div>W: 80It''s almost the end of our tour of China''s most magnificent cities. I hope you''re enjoying every moment with us at Hao Nan Tour Company. Now then, let''s talk about tomorrow. 81We''ll be joining a boat trip down the Li River. Not surprisingly, tickets are sold out! 81Luckily, this is included in our tour package. Finally, 82don''t forget to bring your tour ID card with you everywhere we go, so that you can get the discounts and perks that we''ve arranged for you with local businesses.</div> </div><div><div>W: Chuyến tham quan các thành phố tráng lệ nhất của Trung Quốc đã gần như kết thúc. Tôi hy vọng các bạn đang tận hưởng từng khoảnh khắc với chúng tôi tại Công ty Lữ hành Hao Nan. Bây giờ, chúng ta hãy nói về lịch trình ngày mai. Chúng ta sẽ tham gia một chuyến du ngoạn bằng thuyền trên sông Li. Không có gì ngạc nhiên khi vé đã được bán hết! May mắn thay, việc mua vé đã bao gồm trong gói du lịch của chúng ta. Cuối cùng, đừng quên mang theo thẻ du lịch của các bạn ở mọi nơi chúng ta đến, để các bạn có thể nhận được các chiết khấu và đặc quyền mà chúng tôi đã sắp xếp cho các bạn với những doanh nghiệp địa phương.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619682428.mp3', NULL, 'audio', NULL, 48, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Where are the listeners?', 80, '<p><b>Dịch đáp án:</b></p><p><span>Người nghe ở đâu?</span></p><p><span>A. Tại một triển lãm thương mại</span></p><p><span>B. Tại bữa tiệc gây quỹ</span></p><p><span>C. Trong chuyến đi nghỉ dưỡng của công ty</span></p><p><span>D. Trong chuyến tham quan thành phố</span></p>', 'At a trade show', 'At a fund-rasing banquet', 'On a company retreat', 'On a city tour', 'D', 48, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the speaker imply when she says, "tickets are sold out"?', 81, '<p><b>Dịch đáp án:</b></p><p><span>Người nói có ý gì khi nói "vé đã bán hết"?</span></p><p><span>A. Một sự kiện đã được lên lịch lại</span></p><p><span>B. Một hoạt động phổ biến</span></p><p><span>C. Phòng vé đóng cửa</span></p><p><span>D. Sẽ được hoàn lại tiền</span></p>', 'An event has been rescheduled', 'An activity is popular', 'A ticket office is closed', 'A refund will be issued', 'B', 48, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the speaker remind the listeners to bring?', 82, '<p><b>Dịch đáp án:</b></p><p><span>Người nói nhắc nhở người nghe mang theo điều gì?</span></p><p><span>A. Một chiếc ô</span></p><p><span>B. Một đôi giày thoải mái</span></p><p><span>C. Thẻ căn cước</span></p><p><span>D. Biên lai thanh toán</span></p>', 'An umbrella', 'A pair of comfortable shoes', 'An identification card', 'A receipt for payment', 'C', 48, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (49, 'part4', 48, '<div><div>M: Don''t have time to shop for groceries? Well, you''re in luck. 83Your Food Cart is a new online service that delivers groceries from your local supermarket. In less than five minutes, you''ll be able to place an order to have groceries brought right to your door. 84To get started, create an account at  <a href="http://www.yourfoodcart.com.">www.yourfoodcart.com.</a> Then you can select the groceries you''d like and a delivery time. Our team will shop for you and bring the groceries to your home. And that''s not all: 85if you place your first order before the end of this month, you''ll receive a complimentary bag of locally grown apples. Don''t wait; try Your Food Cart today!</div> </div><div><div>M: Bạn không có thời gian đi mua hàng? Chà, bạn đang gặp may đấy. Your Food Cart là một dịch vụ trực tuyến mới cung cấp hàng tạp hóa từ siêu thị địa phương của bạn. Trong vòng chưa đầy năm phút, , bạn có thể đặt đơn hàng tạp hóa được giao đến tận nhà. Để bắt đầu, hãy tạo một tài khoản tại địa chỉ www.yourfoodcart.com. Sau đó, bạn có thể chọn hàng bạn muốn và thời gian giao hàng. Đội ngũ của chúng tôi sẽ mua sắm cho bạn và mang hàng tạp hóa đến nhà bạn. Và đó không phải là tất cả: nếu bạn đặt đơn hàng đầu tiên trước cuối tháng này, bạn sẽ nhận được một túi táo trồng tại địa phương miễn phí. Đừng chờ đợi; hãy thử Your Food Cart ngay hôm nay!</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619682624.mp3', NULL, 'audio', NULL, 49, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What kind of service is being advertised?', 83, '<p><b>Dịch đáp án:</b></p><p><span>Loại dịch vụ nào đang được quảng cáo?</span></p><p><span>A. Vệ sinh văn phòng</span></p><p><span>B. Nghề may</span></p><p><span>C. Giao hàng tạp hóa</span></p><p><span>D. Cảnh quan</span></p>', 'Office cleaning', 'Tailoring', 'Grocery delivery', 'Landscaping', 'C', 49, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('How can the listeners start using a service?', 84, '<p><b>Dịch đáp án:</b></p><p><span>Người nghe có thể bắt đầu sử dụng dịch vụ bằng cách nào?</span></p><p><span>A. Bằng cách ghé thăm một cửa hàng địa phương</span></p><p><span>B. Bằng cách đặt lịch tư vấn</span></p><p><span>C. Bằng cách gọi đến một số điện thoại</span></p><p><span>D. Bằng cách tạo một tài khoản trực tuyến</span></p>', 'By visiting a local shop', 'By scheduling a consultation', 'By calling a telephone number', 'By creating an account online', 'D', 49, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What special promotion is available?', 85, '<p><b>Dịch đáp án:</b></p><p><span>Có khuyến mãi đặc biệt gì?</span></p><p><span>A. Một món quà miễn phí</span></p><p><span>B. Đảm bảo hoàn lại tiền</span></p><p><span>C. Nâng cấp thành viên</span></p><p><span>D. Báo giá cá nhân</span></p>', 'A free gift', 'A money-back guarantee', 'A membership upgrade', 'A personalized price quote', 'A', 49, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (50, 'part4', 49, '<div><div>M: 86I’d like to welcome Mei Wang, the new staff writer for the arts and culture section of our magazine. For the past five years, Ms. Wang was the lead reporter for the local newspaper in her hometown. Even though she wrote for a small newspaper, 87I think you might recognize her name. In January, she won a prestigious national award for her news story about community theaters in small towns. I''m sure you all are eager to get to know Ms. Wang better. So, 88we''ll be holding a luncheon for her today, and everyone is invited. Please stop by the conference room at noon for soup and sandwiches.</div> </div><div><div>M: Tôi muốn chào mừng Mei Wang, ký giả mới cho mảng văn hóa và nghệ thuật của tạp chí chúng ta. Trong 5 năm qua, cô Wang là phóng viên chính của tờ báo địa phương ở quê nhà. Mặc dù cô ấy viết cho một tờ báo nhỏ, tôi nghĩ rằng các bạn có thể biết tên cô ấy. Vào tháng 1, cô ấy đã giành được một giải thưởng quốc gia danh giá cho câu chuyện tin tức về các rạp hát cộng đồng ở các thị trấn nhỏ. Tôi chắc rằng tất cả các bạn đều mong muốn được hiểu rõ hơn về cô Wang. Vì vậy, chúng ta sẽ tổ chức một bữa tiệc trưa cho cô ấy hôm nay, và mọi người đều được mời. Vui lòng ghé qua phòng hội nghị vào 12 giờ trưa để thưởng thức súp và bánh mì.</div><div><br/></div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619682853.mp3', NULL, 'audio', NULL, 50, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What is the purpose of the talk?', 86, '<p><b>Dịch đáp án:</b></p><p><span>Mục đích của cuộc nói chuyện là gì?</span></p><p><span>A. Cảm ơn tình nguyện viên dự án</span></p><p><span>B. Để giải thích lịch trình sự kiện</span></p><p><span>C. Để giới thiệu nhân viên mới</span></p><p><span>D. Chào mừng một diễn giả khách mời</span></p>', 'To thank project volunteers', 'To explain an event schedule', 'To introduce a new employee', 'To welcome a guest speaker', 'C', 50, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('According to the speaker, why might the listeners be familiar with Ms.Wang?', 87, '<p><b>Dịch đáp án:</b></p><p><span>Theo người nói, tại sao người nghe có thể quen thuộc với cô </span><span>Wang</span><span>?</span></p><p><span>A. Cô ấy đóng vai chính trong một chương trình truyền hình</span></p><p><span>B. Cô ấy đã giành được giải thưởng báo chí</span></p><p><span>C. Cô ấy từng là thực tập sinh tại công ty</span></p><p><span>D. Cô ấy có người thân làm việc cho công ty</span></p>', 'She starred in a television show', 'She won a journalism award', 'She used to be an intern at the company', 'She has relatives who work for the company', 'B', 50, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What event are the listeners encouraged to attend?', 88, '<p><b>Dịch đáp án:</b></p><p><span>Sự kiện nào người nghe được khuyến khích tham dự?</span></p><p><span>A. Cuộc họp hội đồng quản trị</span></p><p><span>B. Một buổi biểu diễn sân khấu</span></p><p><span>C. Một buổi tập</span></p><p><span>D. Bữa trưa của nhân viên</span></p>', 'A board meeting', 'A theater performance', 'A training session', 'A staff luncheon', 'D', 50, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (51, 'part4', 50, '<div><div>W: And in local news, here''s a reminder from the Grantson Community Center about their fund- raising event this Saturday evening. 89The center is holding a comedy festival featuring some of the area’s most well-known comedians. 90Proceeds from the event will help support the center''s project to improve its building. They want to raise funds to paint colorful murals on the outside walls. So come on down and enjoy the show. 91The center''s also looking for a few volunteers to help set up the sound equipment for the show. If you''re able to help with that, please give them a call.</div> </div><div><div>W: Và giờ là tin tức địa phương, lời nhắc từ Trung tâm Cộng đồng Grantson về sự kiện gây quỹ của họ vào tối thứ bảy này. Trung tâm sẽ tổ chức một lễ hội hài kịch với sự góp mặt của một số nghệ sĩ hài nổi tiếng nhất khu vực. Tiền thu được từ sự kiện này sẽ giúp hỗ trợ dự án của trung tâm để cải thiện tòa nhà của họ. Họ muốn gây quỹ để vẽ những bức tranh bích họa đầy màu sắc trên các bức tường bên ngoài. Vì vậy, hãy đến và thưởng thức chương trình. Trung tâm cũng đang tìm kiếm một số tình nguyện viên để giúp lắp đặt thiết bị âm thanh cho buổi biểu diễn. Nếu bạn có thể trợ giúp việc đó, vui lòng gọi cho họ.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_medias (file_url, file_public_id, file_type, `order`, question_group_id, created_at, updated_at) VALUES ('https://s4-media1.study4.com/media/t24_media/sound/1619407464_1619683014.mp3', NULL, 'audio', NULL, 51, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Who will be featured at Saturday''s event?', 89, '<p><b>Dịch đáp án:</b></p><p><span>Ai sẽ xuất hiện tại sự kiện ngày thứ Bảy?</span></p><p><span>A. Nhà điêu khắc</span></p><p><span>B. Nhạc sĩ</span></p><p><span>C. Tác giả</span></p><p><span>D. Diễn viên hài</span></p>', 'Sculptors', 'Musicians', 'Authors', 'Comedians', 'D', 51, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What is money being raised for?', 90, '<p><b>Dịch đáp án:</b></p><p><span>Tiền được quyên góp để làm gì?</span></p><p><span>A. Chương trình sau giờ học</span></p><p><span>B. Một giải đấu thể thao</span></p><p><span>C. Dự án cải tạo tòa nhà</span></p><p><span>D. Phòng khám sức khỏe</span></p>', 'An after-school program', 'A sports tournament', 'A building improvement project', 'A health clinic', 'C', 51, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why are volunteers needed?', 91, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao cần tình nguyện viên?</span></p><p><span>A. Để thiết lập thiết bị</span></p><p><span>B. Bán vé</span></p><p><span>C. Tặng thực phẩm</span></p><p><span>D. Chỉ đạo giao thông</span></p>', 'To set up equipment', 'To sell tickets', 'To donate food', 'To direct traffic', 'A', 51, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (52, 'part4', 51, '<div><div>W: Hi, Omar. 92This is Anusha from Berringer Media Productions. I''m editing the video you wanted made. And, 93after reviewing the film, I, uh, I think we should change some of the colors so that the contrast is more noticeable. I went ahead and created a version with the new colors. Now, 94I know  you were hoping to view a draft this afternoon. But, unfortunately, I''m having some trouble uploading the file. 94So, I''ll call you with an update tomorrow.</div> </div><div><div>W: Chào Omar. Tôi là Anusha ở công ty Berringer Media Productions. Tôi đang chỉnh sửa video mà bạn muốn thực hiện. Và, sau khi xem lại bộ phim, tôi nghĩ chúng ta nên thay đổi một số màu sắc để sự tương phản dễ nhận thấy hơn. Tôi đã tiếp tục và tạo ra một phiên bản với các màu mới. Bây giờ, tôi biết bạn đã muốn xem một bản thảo chiều nay. Tuy nhiên, rất tiếc, tôi đang gặp một số sự cố khi tải tập tin lên. Do đó, tôi sẽ gọi cho bạn để báo tình hình vào ngày mai.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
