INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Which field does the speaker most likely work in?', 92, '<p><b>Dịch đáp án:</b></p><p><span>Người nói</span><span> có khả năng làm việc trong lĩnh vực nào nhất?</span></p><p><span>A. Sản xuất phim</span></p><p><span>B. Thiết kế nội thất</span></p><p><span>C. Lập trình máy tính</span></p><p><span>D. Báo chí phát sóng</span></p>', 'Film production', 'Interior design', 'Computer programming', 'Broadcast journalism', 'A', 52, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What improvement did the speaker make?', 93, '<p><b>Dịch đáp án:</b></p><p><span>Người nói đã thực hiện cải tiến gì?</span></p><p><span>A. Cô ấy đã thuê thêm nhân viên</span></p><p><span>B. Cô ấy đã sử dụng phần mềm khác</span></p><p><span>C. Cô ấy đã thay đổi một số màu sắc</span></p><p><span>D. Cô ấy đã nói chuyện với một chuyên gia</span></p>', 'She hired more staff', 'She used different software', 'She changed some colors', 'She talked to a specialist', 'C', 52, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why does the speaker say, "I''m having some trouble uploading the file"?', 94, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao người nói lại nói: "Tôi gặp một số khó khăn khi tải tệp lên"?</span></p><p><span>A. Để yêu cầu một số thiết bị mới</span></p><p><span>B. Khiếu nại về một chính sách</span></p><p><span>C. Để nhờ giúp đỡ</span></p><p><span>D. Để giải thích sự chậm trễ</span></p>', 'To request some new equipment', 'To complain about a policy', 'To ask for help', 'To explain a delay', 'D', 52, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (53, 'part4', 52, '<div><p>M: Good job, everyone. Today''s endof-season sale was a huge success, and you all did a good job assisting customers. 95We''re almost completely sold-out of winter accessories. There are only a few gloves and scarves left, and coats are selling well. This is great because 96we''re expecting the first shipment of spring clothes next Tuesday. We really need to clear out as much space on our shelves as possible. There''s been one change to tomorrow''s schedule – 97Patrick will be out, so I''ll be filling in for him myself.<br/></p></div><div><div>M: Làm tốt lắm mọi người. Đợt giảm giá cuối mùa hôm nay đã thành công rực rỡ và tất cả các bạn đã làm rất tốt việc hỗ trợ khách hàng. Chúng ta gần như đã bán hết các phụ kiện mùa đông. Chỉ còn một số ít găng tay và khăn quàng cổ, còn áo choàng đang bán rất chạy. Điều này thật tuyệt vì chúng ta đang đợi lô đầu tiên quần áo mùa xuân đầu tiên vào thứ ba tới. Chúng ta thực sự cần phải giải phóng càng nhiều không gian trên kệ càng tốt. Có một sự thay đổi đối với lịch trình ngày mai – Patrick sẽ không ở đây, vì vậy chính tôi sẽ làm thay ca cho anh ấy.</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
