INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Where does the speaker most likely work?', 95, '<p><b>Dịch đáp án:</b></p><p><span>Người nói có khả năng làm việc ở đâu nhất?</span></p><p><span>A. Tại hiệu sách</span></p><p><span>B. Tại cửa hàng điện tử</span></p><p><span>C. Tại một cửa hàng đồ gia dụng</span></p><p><span>D. Tại cửa hàng quần áo</span></p>', 'At a bookshop', 'At an electronics store', 'At a home-improvement store', 'At a clothing store', 'D', 53, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What will happen next Tuesday?', 96, '<p><b>Dịch đáp án:</b></p><p><span>Điều gì sẽ xảy ra vào thứ ba tới?</span></p><p><span>A. Hàng mới sẽ đến</span></p><p><span>B. Cửa hàng sẽ đóng cửa nghỉ lễ</span></p><p><span>C. Nhân viên mới sẽ bắt đầu làm việc</span></p><p><span>D. Một người quản lý khu vực sẽ đến thăm</span></p>', 'New inventory will arrive', 'The store will close for a holiday', 'New employees will begin working', 'A district manager will visit', 'A', 53, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Look at the graphic. What job will the speaker do tomorrow?', 97, '<p><b>Dịch đáp án:</b></p><p><span>Nhìn vào đồ họa. Người nói sẽ làm công việc gì vào ngày mai?</span></p><p><span>A. Người chào hỏi</span></p><p><span>B. Đại diện bán hàng</span></p><p><span>C. Nhân viên thu ngân</span></p><p><span>D. Nhân viên kho</span></p>', 'Greeter', 'Sales representative', 'Cashier', 'Stock clerk', 'C', 53, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO question_groups (id, part, group_index, transcript, passage, toeic_test_id, created_at, updated_at) VALUES (54, 'part4', 53, '<div><p>M: Hi, Salma. This is Alberto. 98I''m still out of town training staff at our store in Manila. I''ll here training them until the end of this week. I just remembered that 99I was supposed to place the order for printer cartridges for our offices, but I forgot to do that. Would you be able to order them? You can find the item number for the printer cartridges on the list on my desk. Oh, one more thing... 100Can you let me know when you''re available next week? We need to start interviewing for a new receptionist when I get back to the office. Thanks!<br/></p></div><div><div>M: Chào, Salma. Đây là Alberto. Tôi vẫn đang ở ngoài thành phố để đào tạo nhân viên tại cửa hàng của chúng ta ở Manila. Tôi sẽ đào tạo họ ở đây cho đến cuối tuần này. Tôi vừa nhớ rằng tôi đã phải đặt hộp mực máy in cho văn phòng của chúng ta, nhưng tôi đã quên làm điều đó. Bạn có thể đặt chúng không? Bạn có thể tìm thấy mã số món hàng của hộp mực máy in trong danh sách trên bàn làm việc của tôi. Ồ, còn một điều nữa... Bạn có thể cho tôi biết tuần sau bạn rảnh khi nào? Chúng ta cần bắt đầu phỏng vấn cho một nhân viên lễ tân mới khi tôi trở lại văn phòng. Cảm ơn!</div> </div>', NULL, 1, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
