INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why is the speaker traveling?', 98, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao người nói lại đi du lịch?</span></p><p><span>A. Anh ấy đang đi nghỉ</span></p><p><span>B. Anh ấy đang giao hàng</span></p><p><span>C. Anh ấy đang kiểm tra tài sản</span></p><p><span>D. Anh ấy đang tiến hành đào tạo</span></p>', 'He is taking a vacation', 'He is making a delivery', 'He is inspecting properties', 'He is conducting training', 'D', 54, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Look at the graphic. Which item number should the listener order?', 99, '<p><b>Dịch đáp án:</b></p><p><span>Nhìn vào đồ họa. Người nghe nên đặt hàng món hàng nào?</span></p><p><span>A. NT-105</span></p><p><span>B. FF-200</span></p><p><span>C. A-888</span></p><p><span>D. C-250</span></p>', 'NT-105', 'FF-200', 'A-888', 'C-250', 'A', 54, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does the speaker say will happen next week?', 100, '<p><b>Dịch đáp án:</b></p><p><span>Người nói nói điều gì sẽ xảy ra vào tuần tới?</span></p><p><span>A. Một số giá sẽ được giảm giá</span></p><p><span>B. Một số người xin việc sẽ được phỏng vấn</span></p><p><span>C. Một sản phẩm mới sẽ được phát hành</span></p><p><span>D. Văn phòng sẽ chuyển đến địa điểm mới</span></p><p><span>Năm ngoái, nó đã thu hút hơn 3.000 người tham dự từ Úc và khắp nơi trên thế giới.</span></p>', 'Some prices will be discounted', 'Some job applicants will be interviewed', 'A new product will be released', 'An office will move to a new location', 'B', 54, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
