INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('The manager often leads new employees through the safety procedures_____', 102, '<p><span>Giải thích: Câu đã đủ thành phần, chọn đại từ phản thân.</span></p><p><span>Dịch: Người quản lý thường tự mình hướng dẫn cho những nhân viên mới về quy trình an toàn</span></p><p><span>Từ vựng:</span></p><p><span>- lead (v): dẫn dắt</span></p><p><span>- procedure (n): quy trình, thủ tục</span></p>', 'her', 'herself', 'hers', 'she', 'B', 56, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
