INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('The corporate fitness center is equipped_____fourteen stationary bicycles', 103, '<p><span>Giải thích: sử dụng cấu trúc "be equipped + with" nghĩa là "được trang bị (với)"</span></p><p><span>Dịch: Trung tâm thể dục của tập đoàn được trang bị 14 máy đạp xe đạp</span></p><p><span>Từ vựng:</span></p><p><span>- corporate (n): tập đoàn</span></p><p><span>- stationary bicycle (n) : máy đạp xe đạp</span></p><p><span>- at (pre) ở tại (vị trí) vào lúc (thời gian)</span></p><p><span>- on (pre) ở trên (vị trí), vào lúc, khi (thời gian)</span></p><p><span>- about (prep) về cái gì đó (adv) khoảng</span></p>', 'at', 'on', 'with', 'about', 'C', 57, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
