INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Arnby Cable_____$25 to all Internet subscribers after the weeklong service interruption', 105, '<p><span>Giải thích: Dựa trên nghĩa để chọn.</span></p><p><span>Dịch: Cáp Amby đã hoàn trả 25 đô cho toàn bộ người đăng ký internet sau sự gián đoạn dịch vụ kéo dài cả tuần.</span></p><p><span>Từ vựng:</span></p><p><span>- subscriber (n): người đăng ký</span></p><p><span>- interruption (n): sự gián đoạn</span></p><p><span>- refund (v): hoàn trả tiền</span></p><p><span>- divide ... into (v): chia ... thành</span></p><p><span>- deposit (v): đặt cọc, ký quỹ</span></p><p><span>- </span><span>accepted</span><span> (v) chấp thuận</span></p>', 'refunded', 'accepted', 'divided', 'deposited', 'A', 59, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
