INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('The board of directors will meet next Monday to examine the current hiring____', 106, '<p><span>Giải thích: Dựa trên nghĩa để chọn</span></p><p><span>Dịch: Ban giám đốc sẽ họp vào thứ hai tuần sau để kiểm tra chính sách tuyển dụng hiện tại.</span></p><p><span>Từ vựng:</span></p><p><span>- examine (v): kiểm tra</span></p><p><span>- current (adj): hiện tại</span></p><p><span>- assembly (n): sự lắp ráp</span></p><p><span>- intent (n): ý định</span></p><p><span>- policy (n): chính sách</span></p>', 'purpose', 'intent', 'assembly', 'policy', 'D', 60, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
