INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Passengers should not leave their seats____a flight attendant gives them permissinon to do so', 107, '<p><span>Giải thích: Dựa trên nghĩa để chọn</span></p><p><span>Dịch: Các hành khách không được rời khỏi ghế của họ trừ khi các tiếp viên hàng không cho phép họ làm vậy.</span></p><p><span>Từ vựng:</span></p><p><span>- permission (n): sự cho phép</span></p><p><span>- passenger (n): hành khách</span></p><p><span>- flight attendant (n): tiếp viên hàng không</span></p><p><span>- unless + MD. trừ khi</span></p><p><span>- rather + adj/adv (adv): khá</span></p><p><span>- instead (adv): thay vào đó</span></p><p><span>- otherwise (adv): mặt khác</span></p>', 'unless', 'reather', 'instead', 'otherwise', 'A', 61, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
