INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('The building''s new ventilation system circulates heat much____than before', 109, '<p><span>Giải thích: much là trạng từ dùng để nhấn mạnh so sánh hơn.</span></p><p><span>Dịch: Hệ thống thông gió mới của tòa nhà luân chuyển nhiệt đồng đều hơn trước đây.</span></p><p><span>Từ vựng:</span></p><p><span>- ventilation (n): sự thông gió</span></p><p><span>- circulate (v): lưu hành, tuần hoàn</span></p><p><span>- evenly (adv): đồng đề</span><span>u</span></p>', 'even', 'most even', 'evenly', 'more evenly', 'D', 63, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
