INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('All vacation requests must be made to your supervisor____the requested date', 112, '<p><span>Giải thích: Dựa trên nghĩa để chọn</span></p><p><span>Dịch: Tất cả các yêu cầu xin nghỉ phải được gửi cho giám sát viên trước ngày xin nghỉ.</span></p><p><span>Từ vựng:</span></p><p><span>- vacation (n): nghỉ phép/kỳ nghỉ</span></p><p><span>- prior to = before (adj): trước</span></p><p><span>- except for (v): ngoại trừ</span></p><p><span>- previously (adv): trước đây</span></p>', 'prior to', 'except for', 'previously', 'because', 'A', 66, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
