INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Construction at Langhall Plaza is going so well that shops might open before expected___date', 114, '<p><span>Giải thích: Dựa trên nghĩa để chọn</span></p><p><span>Dịch: Việc xây dựng tại </span><span>Langhall</span><span> Plaza đang tiến triển tốt cho nên các cửa hàng có thể mở cửa trước ngày hoàn thành dự kiến.</span></p><p><span>Từ vựng:</span></p><p><span>- construction (n): việc xây dựng</span></p><p><span>- expected (adj): được dự kiến</span></p><p><span>- completion (n): sự hoàn thành</span></p><p><span>- selection (n): sự lựa chọn</span></p><p><span>- decision (n): sự định đoạt</span></p>', 'completion', 'selection', 'decision', 'option', 'A', 68, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
