INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Mr. Culter has been asked to _____ domestic sales of low-calorie beverages', 117, '<p><span>Giải thích: Dựa trên nghĩa để chọn</span></p><p><span>Dịch: Ông Cutler đã được yêu cầu giám sát doanh số bán hàng nội địa của các thức uống ít năng lượng</span></p><p><span>Từ vựng:</span></p><p><span>- ask someone to V: yêu cầu ai làm gì</span></p><p><span>- domestic (adj): nội địa</span></p><p><span>- oversee = supervise (v): giám sát</span></p><p><span>- succeed (v): thành công</span></p><p><span>- possess (v): sở hữu</span></p><p><span>- persist (v): kiên trì</span></p>', 'oversee', 'possess', 'succeed', 'persist', 'A', 71, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
