INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Cranford Culinary Academy offers 35 different classes for ____chefs', 118, '<p><span>Giải thích: aspiring + N chỉ người: người có khao khát, đang xây dựng sự nghiệp</span></p><p><span>Dịch: Học viện ẩm thực Cranford cung cấp 35 lớp học khác nhau cho những người muốn trở thành đầu bếp.</span></p><p><span>Từ vựng:</span></p><p><span>- culinary (adj): thuộc về ẩm thực</span></p><p><span>- academy (n): học viện</span></p>', 'aspires', 'aspirations', 'aspiring', 'to aspire', 'C', 72, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
