INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('____, items sold at the Scottville Craft Fair are unique and of very high quality', 119, '<p><span>Giải thích: Dựa trên nghĩa để chọn</span></p><p><span>Dịch: Thường thì, các mặt hàng bán tại hội chợ đồ thủ công Scottville là độc đáo và có chất lượng rất cao</span></p><p><span>Từ vựng:</span></p><p><span>- fair (n): hội chợ</span></p><p><span>- </span><span>uniqure</span><span> (adj): độc đáo</span></p><p><span>- fairly = quite + adj/adv: khá</span></p><p><span>- typically = normally: bình thường, thông thường</span></p><p><span>- simply = just: đơn giản là</span></p><p><span>- entirely: toàn bộ</span></p>', 'Fairly', 'Typically', 'Simple', 'Entirely', 'B', 73, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
