INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Data from the finance deparment was used to____predict the company''s future expenses', 124, '<p><span>Giải thích: Dựa trên nghĩa/ngữ pháp để chọn. Câu này chọn adv vì đứng giữa giữa to V</span></p><p><span>Dịch: Dữ liệu từ phòng tài chính đã được sử dụng để dự đoán tốt hơn về chi phí của công ty trong tương lai.</span></p><p><span>Từ vựng:</span></p><p><span>- finance (</span><span>n,v</span><span>): tài chính</span></p><p><span>- predict = foresee = forecast (v): dự đoán</span></p><p><span>- expenses ~ costs (n): chi phí</span></p><p><span>- better (a,adv): tốt hơn</span></p>', 'either', 'ever', 'yet', 'better', 'D', 78, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
