INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Every year Arrow Mill, Inc., processes a ___ amount of grain', 126, '<p><span>Giải thích: a large/substantial/huge amount of </span><span>st</span><span>: một lượng lớn…</span></p><p><span>Dịch: Mỗi năm Arrow Mill Inc chế biến một lượng ngũ cốc lớn.</span></p><p><span>Từ vựng:</span></p><p><span>- process (v): chế biến, xử lý</span></p><p><span>- grain (n): ngũ cốc, lương thực</span></p><p><span>- durable = long-lasting (adj): bền</span></p><p><span>- direct (adj): trực tiếp</span></p><p><span>- resolute (adj): quả quyết</span></p><p><span>- substantial (adj): đáng kể, lớn</span></p>', 'durable', 'direct', 'resolute', 'substantial', 'D', 80, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
