INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why was Ms. Mohlmann unhappy with S-Tor''s customer service?', 170, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao bà Mohlmann không hài lòng với dịch vụ chăm sóc khách hàng của S-Tor?</span></p><p><span>A. Vấn đề của cô ấy mất quá nhiều thời gian để giải quyết</span></p><p><span>B. Hướng dẫn đưa ra không rõ ràng</span></p><p><span>C. Nhân viên bộ phận trợ giúp không thân thiện</span></p><p><span>D. Cô ấy cảm thấy bị áp lực phải mua hàng</span></p>', 'Her issue took too long to resolve', 'The instructions given were not clear', 'Help-desk employees were unfriendly', 'She felt pressured to make a purchase', 'A', 97, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
