INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('Why are residents encouraged to attend a meeting on Monday, April 17?', 197, '<p><b>Dịch đáp án:</b></p><p><span>Tại sao người dân được khuyến khích tham dự cuộc họp vào Thứ Hai, ngày 17 tháng 4?</span></p><p><span>A. Được đào tạo về nói trước công chúng</span></p><p><span>B. Để thể hiện sự ủng hộ đối với một số chính trị gia thành phố</span></p><p><span>C. Để có được thông tin về đề xuất quy hoạch</span></p><p><span>D. Gặp vài người hàng xóm mới</span></p>', 'To receive training in public speaking', 'To show support for some city politicians', 'To get information about a zoning proposal', 'To meet some new neighbors', 'C', 103, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What HHNA goal is most likely being challenged by the Chenaux Company?', 198, '<p><b>Dịch đáp án:</b></p><p><span>Mục tiêu HHNA nào có nhiều khả năng bị Công ty Chenaux thách thức nhất?</span></p><p><span>A. Mục tiêu 1</span></p><p><span>B. Mục tiêu 2</span></p><p><span>C. Mục tiêu 3</span></p><p><span>D. Mục tiêu 4</span></p>', 'Goal 1', 'Goal 2', 'Goal 3', 'Goal 4', 'B', 103, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What is suggested about Ms. Redman?', 199, '<p><b>Dịch đáp án:</b></p><p><span>Điều gì được gợi ý về cô Redman?</span></p><p><span>A. Cô ấy đã phát biểu tại một cuộc họp vào tháng Tư</span></p><p><span>B. Cô ấy sở hữu một doanh nghiệp ở cảng Hulani</span></p><p><span>C. Cô ấy chịu trách nhiệm phân phát chương trình họp</span></p><p><span>D. Cô ấy viết bài cho một bản tin</span></p>', 'She spoke at a meeting in April', 'She owned a business in Hulani Harbor', 'She is responsible for distributing the meeting agendas', 'She writes articles for a newsletter', 'A', 103, '2025-05-20 21:47:03', '2025-05-20 21:47:03');
INSERT INTO questions (question, question_number, explanation, A, B, C, D, correct_answer, question_group_id, created_at, updated_at) VALUES ('What does Ms. Redman suggest qualifies her for the representative position?', 200, '<p><b>Dịch đáp án:</b></p><p><span>Cô Redman gợi ý điều gì khiến cô đủ tiêu chuẩn cho vị trí đại diện?</span></p><p><span>A. Bằng đại học của cô ấy</span></p><p><span>B. Những ý tưởng lãnh đạo mới mẻ của cô ấy</span></p><p><span>C. Nhận thức của cô ấy về các vấn đề quốc gia</span></p><p><span>D. Kinh nghiệm của cô trong việc hỗ trợ các nhóm địa phương</span></p>', 'Her university degree', 'Her fresh leadership ideas', 'Her awareness of national issues', 'Her experience in supporting local groups', 'D', 103, '2025-05-20 21:47:03', '2025-05-20 21:47:03');